import { useState, useContext } from "react";
import { UserContext } from "../context/UserProvider";

export default function VoteTracker(props) {
  const { _id, upvotes, downvotes } = props;
  const { userAxios } = useContext(UserContext);
  const [upvote, setUpvote] = useState(upvotes);
  const [downvote, setDownvote] = useState(downvotes);


  function upVotePost(postId) {
    userAxios
      .put(`/main/posts/upVote/${postId}`)
      .then((res) => {
        console.log(res.data);
        setAllPosts((prevPosts) =>
          prevPosts.map((post) => (postId !== post._id ? post : res.data))
        );
        setUserState((prevUserState) => ({
          ...prevUserState,
          posts: prevUserState.posts.map((post) =>
            postId !== post._id ? post : res.data
          ),
        }));
      })
      .catch((err) => console.log(err));
  }
  // function upVote() {
  //   userAxios
  //    .put(`/api/polipost/${_id}/upvote`)
  //    .then((res) => console.log(res.data))
  //    .catch((err) => console.log(err.response.data.errMsg))
  // }

  function downVote() {
    userAxios
      .put(`/api/polipost/${_id}/downvote`)
      .then((res) => console.log(res.data))
      .catch((err) => console.log(err.response.data.errMsg));
  }

  const handleUpvote = () => {
    setUpvote((prevUpVotes) => prevUpVotes + 1)
    upVote(upvotes, _id);
  };

  const handleDownvotes = () => {
    setDownvote((prevDownvotes) => prevDownvotes + 1);
    downVote(downvotes, _id);
  };

  return (
    <div id="vote-tracker">
      <p>Upvotes: {upvote.length}</p>
      <button onClick={handleUpvote}>UP</button>
      <p>Downvotes: {downvote.length}</p>
      <button onClick={handleDownvotes}>DOWN</button>
    </div>
  );
}
