import { useState, useContext } from "react";
import { UserContext } from "../context/UserProvider";

export default function VoteTracker({
  _id,
  initialUpvotes = 0,
  initialDownvotes = 0,
}) {
  const { userAxios } = useContext(UserContext);
  const [upvotes, setUpvotes] = useState(initialUpvotes);
  const [downvotes, setDownvotes] = useState(initialDownvotes);

  function upVote(upvotes, postId) {
    userAxios
      .put(`/api/polipost/${postId}`, upvotes)
      .then((res) => console.log(res))
      .catch((err) => console.log(err.response.data.errMsg));
  }

  function downVote(downvotes, postId) {
    userAxios
      .put(`/api/polipost/${postId}`, downvotes);
      then((res) => console.log(res))
      .catch((err) =>
      console.log(err.response.data.errMsg)
    );
  }

  const handleUpvote = () => {
    setUpvotes((prevUpvotes) => prevUpvotes + 1);
    upVote();
  };

  const handleDownvotes = () => {
    setDownvotes((prevDownvotes) => prevDownvotes + 1);
    // update object in database
  };

  return (
    <div id="vote-tracker">
      <p>Upvotes: {upvotes}</p>
      <button onClick={handleUpvote}>UP</button>
      <p>Downvotes: {downvotes}</p>
      <button onClick={handleDownvotes}>DOWN</button>
    </div>
  );
}
